import React, { useState } from "react";
import "./Testimonials.css";
import {testimonialsData} from "../../data/testimonialsData"
import LeftArrow from "../../assets/leftArrow.png";
import RightArrow from "../../assets/rightArrow.png";

function Testimonials(){
    const [selected,setSelected]=useState(0);
    const length=testimonialsData.length;
    return(
        <div className="testimonial">
            <div className="left-t">
                <span>Testimonials</span>
                <span className="stroke-text">What They</span>
                <span>Say About Us</span>
                <span>{testimonialsData[selected].review}</span>
                <div><span>{testimonialsData[selected].name}</span>
                <span>-{testimonialsData[selected].status}</span></div>
            </div>
            <div className="right-t">
                <div className="arrows">
                    <img src={LeftArrow} onClick={selected===0?setSelected(length-1):setSelected((prev)=>(
                        selected-1
                    ))}/>
                    <img src={RightArrow}/>
                </div>
                <div></div>
                <div></div>
                <img src={testimonialsData[selected].image}/>
            </div>
        </div>
    )
}
export default Testimonials;
